mapper.insert:
insert into board(
bd_no, bd_title, bd_contents, bd_writer, bd_regdate, bd_hitcount, em_no
)
values(
bd_seq.nextval, #{title}, #{contents}, #{writer}, sysdate, 0, ${emNo}
)

mapper.select:
select bd_no as bdNo, bd_title as title, bd_contents as contents, bd_writer as writer, bd_regdate as regdate, bd_hitcount as hitcount, em_no as emNo from board
${item} LIKE #{searchKey}
order by bd_no desc
